import{_ as s,c as e,o,m as t}from"./chunks/framework.4aTu-Nia.js";const R=JSON.parse('{"title":"","description":"","frontmatter":{},"headers":[],"relativePath":"docs/00 创作中/VRP 问题.md","filePath":"docs/00 创作中/VRP 问题.md"}'),n={name:"docs/00 创作中/VRP 问题.md"},c=t("p",null,"车辆路径优化问题（Vehicle Routing Problem, VRP）是指为一系列装货点和卸货点规划适当的路线，使车辆依次通过点位，在满足一定的约束条件（如货物需求量、 发送量、交发货时间、车辆容量等限制）下，达到一定的目标，如路程最短、费用最少、时间最少或使用车辆数最少等。",-1),a=t("p",null,"下图为生成车辆数为3时总距离最小的路线",-1),i=t("p",null,[t("img",{src:"https://muchuang-img.oss-cn-beijing.aliyuncs.com/20241229204539.png",alt:""})],-1),_=t("p",null,"美团配送对实时性要求极高，是秒级的即时调度问题，但对最优性的要求不高/弱于物流场景",-1),l=t("p",null,"调度问题被分解为骑手路径优化和订单分配方案的优化。骑手路径优化问题要解决的是在新订单分配至骑手后，确定骑手的最佳配送线路；而订单分配优化是把一批订单分配至相应的骑手，使得我们关注的指标最优。两者的关系是：订单分配优化算法进行初始的订单分配，然后路径优化算法获取各骑手的最佳行驶路线，订单分配优化算法根据骑手路径优化结果调整分配方案，这两个层次不断反复迭代。",-1),r=t("p",null,[t("img",{src:"https://muchuang-img.oss-cn-beijing.aliyuncs.com/20241229211623.png",alt:""})],-1),d=[c,a,i,_,l,r];function p(m,u,h,g,f,P){return o(),e("div",null,d)}const $=s(n,[["render",p]]);export{R as __pageData,$ as default};
